    } */

    return (
        <div>
             <table>
                <tbody>
                    <tr>
                        <th>Prenom</th>
                        <th>Nom</th>
                        <th>Sexe</th>
                        <th>Numero</th>
                        <th>Age</th>
                        <th>Adress</th>
                        <th>Poids</th>
                        <th>Pression Arterielle</th>
                        <th>Symptome 1</th>
                        <th>Symptome 2</th>
                        <th>Symptome 3</th>
                        <th>Modifier</th>
                        <th>Supprimer</th>
                    </tr>  
                {listOfUser.map((user, index) => (
                    <tr key={index}>
                        <td>{user.nom}</td>
                        <td>{user.prenom}</td>
                        <td>{user.sexe}</td>
                        <td>{user.numero}</td>                            
                        <td>{user.age}</td>
                        <td>{user.address}</td>
                        <td>{user.poids}</td>
                        <td>{user.pression_arterielle}</td>
                        <td>{user.symptome_1}</td>
                        <td>{user.symptome_2}</td>
                        <td>{user.symptome_3}</td>
                        <td>
                            <Link to={`/Edit/${user._id}`}>
                                <button className="edit">Edit</button>
                            </Link>
                        </td>
                        <td><button className="delete"/*  onClick={()=>deleteOneUser()} */>delete</button></td>
                    </tr>
                ))}
                </tbody>
            </table>

            {/* {users.map((user) => (
