import React from 'react';
import { Form, Button } from "react-bootstrap";
import { useHistory, useParams } from 'react-router-dom';
import { editUser, getUsers } from "../api";

const initialValue = {
    prenom: '',
    nom: '',
    sexe: '',
    numero: '',
    age:'',
    adress:'',
    poids:'',
    pression_arterielle:'',
    symptome_1:'',
    symptome_2:'',
    symptome_3:''
}

function Edit() {

    const [user, setUser] = useState(initialValue);
    const { prenom, nom, sexe, numero, age, adress, poids,pression_arterielle,symptome_1,symptome_2,symptome_3 } = user;
    const { id } = useParams();
    let history = useHistory();

    useEffect(() => {
        loadUserDetails();
    }, []);

    const loadUserDetails = async() => {
        const response = await getUsers(id);
        setUser(response.data);
    }

    const editUserDetails = async() => {
        const response = await editUser(id, user);
        history.push('/all');
    }

    const onValueChange = (e) => {
        console.log(e.target.value);
        setUser({...user, [e.target.name]: e.target.value})
    }

    return ( 
        <div >
        <Form className='formulaire'>
            <Form.Label> <h1>Modifier information du patient</h1> </Form.Label>

            <Form.Control type="text" placeholder="Entrer prenom" name='prenom' />
            <br/><br/>

            <Form.Control type="text" placeholder="Entrer nom" name='nom' />
            <br/><br/>

            <Form.Control type="text" placeholder="Entrer sexe" name='sexe' />
            <br/><br/>

            <Form.Control type="number" placeholder="Enter number" name='numero' />
            <br/><br/>

            <Form.Control type="number" placeholder="Entrer l'age" name='age' />
            <br /><br />

            <Form.Control type="text" placeholder="Entrer l'adresse" name='adress' />
            <br /><br />

            <Form.Control type="number" placeholder="Entrer le poids" name='poids'/>
            <br /><br />

            <Form.Control type="number" placeholder="Entrer la pression artÃ©riellle" name='pression_arterielle' />
            <br /><br />

            <Form.Control type="text" placeholder="Entrer le 1eme symptome" name='symptome_1' />
            <br /><br />

            <Form.Control type="text" placeholder="Entrer le 2eme symptome" name='symptome_2' />
            <br /><br />

            <Form.Control type="text" placeholder="Entrer le 3eme symptome" name='symptome_3' />
            <br /><br />
            <Button variant="primary" type="submit" onClick={()=>editUser()}>Modifier</Button>
            <Button variant="primary" type="cancel">Annuler</Button>
        </Form>
    </div>
     )
}

export default Edit;
