import React, { useState, useEffect} from "react";
import { getUsers, deleteUser } from "../api";
import { Link } from "react-router-dom";
import './Liste.css';

function Liste() {

    const [users, setUsers] = useState();

    const getAllUsers = async () => {
        let response = await getUsers();
        setUsers(response);
    }
    useEffect(() => {
        getAllUsers();
    }, []);

    console.log();

    const deleteOneUser= async (id)=>{
        await deleteUser(id)
    }

/*     const arrUser=Object.values(users)
 */
    return (
        <div>
            <table>
                <tbody>
                    <tr>
                        <th>Prenom</th>
                        <th>Nom</th>
                        <th>Sexe</th>
                        <th>Numreo</th>
                        <th>Age</th>
                        <th>Adress</th>
                        <th>Poids</th>
                        <th>Pression Arterielle</th>
                        <th>Symptome 1</th>
                        <th>Symptome 2</th>
                        <th>Symptome 3</th>
                        <th>Modifier</th>
                        <th>Supprimer</th>
                    </tr>  
                {users.map((user, index) => (
                    <tr key={index}>
                        <td>{user.nom}</td>
                        <td>{user.prenom}</td>
                        <td>{user.sexe}</td>
                        <td>{user.numero}</td>                            
                        <td>{user.age}</td>
                        <td>{user.address}</td>
                        <td>{user.poids}</td>
                        <td>{user.pression_arterielle}</td>
                        <td>{user.symptome_1}</td>
                        <td>{user.symptome_2}</td>
                        <td>{user.symptome_3}</td>
                        <td>
                            <Link to={`/Edit/${user._id}`}>
                                <button className="edit">Edit</button>
                            </Link>
                        </td>
                        <td><button className="delete" onClick={()=>deleteOneUser()}>delete</button></td>
                    </tr>
                ))}
                </tbody>
            </table>



            {/* {users.map((user) => (
                <div>
                    <div>{user.id}</div>
                    <div>{user.name}</div>
                    <div>{user.username}</div> 
                    <div>{user.email}</div> 
                    <div>{user.phone}</div> 
                </div>               
            ))} */}

    </div>
     )
}

    export default Liste;


































